#!/bin/bash
set -euo pipefail

if docker build --no-cache --build-arg PYTHON_VERSION=3.6.5 --build-arg DISTRO=stretch --tag discord.py:build2-3.6.5-stretch --tag ${DOCKER_UPLOAD_REPOSITORY:-gorialis}/discord.py:build2-3.6.5-stretch .; then export DOCKER_DISCORD_PY_OK="${DOCKER_DISCORD_PY_OK:-.}\nBUILD build2-3.6.5-stretch"; else export DOCKER_DISCORD_PY_FAILED="${DOCKER_DISCORD_PY_FAILED:-.}\nBUILD build2-3.6.5-stretch"; fi
if docker build --no-cache --build-arg PYTHON_VERSION=3.6.5 --build-arg DISTRO=slim-stretch --tag discord.py:build2-3.6.5-slim-stretch --tag ${DOCKER_UPLOAD_REPOSITORY:-gorialis}/discord.py:build2-3.6.5-slim-stretch .; then export DOCKER_DISCORD_PY_OK="${DOCKER_DISCORD_PY_OK:-.}\nBUILD build2-3.6.5-slim-stretch"; else export DOCKER_DISCORD_PY_FAILED="${DOCKER_DISCORD_PY_FAILED:-.}\nBUILD build2-3.6.5-slim-stretch"; fi
if docker build --no-cache --build-arg PYTHON_VERSION=3.6.5 --build-arg DISTRO=alpine --tag discord.py:build2-3.6.5-alpine --tag ${DOCKER_UPLOAD_REPOSITORY:-gorialis}/discord.py:build2-3.6.5-alpine .; then export DOCKER_DISCORD_PY_OK="${DOCKER_DISCORD_PY_OK:-.}\nBUILD build2-3.6.5-alpine"; else export DOCKER_DISCORD_PY_FAILED="${DOCKER_DISCORD_PY_FAILED:-.}\nBUILD build2-3.6.5-alpine"; fi
